        <script type="text/javascript">
        $(document).ready(function(){
         getWeather();
        })
        function getWeather(){
            $.ajax({
                url: "/getWeather.jspx",
                type: 'GET',
                dataType: 'JSON',//here
                success: function (data) {
                    if(data.status == 'success'){
                        var wtemp = data.results[0].weather_data;
                        for(var i in wtemp) { //循环后台传过来的Json数组
                            if(i<1){
                            $("#qxsweather"+i).text(wtemp[i].weather);
                            var date = wtemp[i].date;
                            var t = date.substring(date.lastIndexOf("：")+1);
                            var w=t.substring(0,t.lastIndexOf(")"));
                            $("#qxstemperature"+i).text(w);
                            var wendu = wtemp[i].dayPictureUrl;
                            var t = "/r/cms/www/zx/img/tianqi/b_"+wendu.substring(wendu.lastIndexOf("/")+1);
                            $("#qxsweatherimg"+i).attr("src",t);
                            }
                        }
                    }else{
                        //alert('数据错误');
                    }
                },
                error: function(data){
                    //alert('请求错误');
                }
            });
        }
        </script>
